import React from 'react'
import styled from 'styled-components'
import { breakpoint } from '@aragon/ui'

import thumbnail from './assets/thumbnail.jpg'

const medium = css => breakpoint('medium', css)
const large = css => breakpoint('large', css)

const StyledImg = styled.img`
  display: block;
  width: 100%;
  min-width: 320px;
  max-width: 1140px;
  height: auto;
  text-align: center;
  border-radius: 3px 3px 0 0;
  box-shadow: 0 4px 64px rgba(149, 149, 149, 0.2);
  filter: brightness(66%);
  margin-bottom: 5rem;
  ${medium('margin-bottom: 12.5rem')};
`

const StyledDiv = styled.div`
  position: relative;
  width: 100%;
  transition: all 0.5s ease;
  margin-left: 50%;
  transform: translateX(-50%);
  flex-shrink: 0;
  margin-top: 40px;
  ${medium('margin-top: 75px')};

  .icon {
    cursor: pointer;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-repeat: no-repeat;
    background-position: 50% 33%;
    background-size: 10%;
    filter: invert(1);
    transition: all 0.5s ease;
    background-image: url("data:image/svg+xml;base64,");

    &:hover {
      opacity: 0.5;
    }
  }

  .hidden {
    display: none;
  }

  #player {
    position: absolute;
    z-index: 1000;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    width: 100%;
	  height: 100%;
  }

  &.playing {
    margin-bottom: 25rem;
  }
`

class Video extends React.Component {
  constructor(props) {
    super(props)
    this.state = { hidden: true }
    this.playVideo = this.playVideo.bind(this)

    const tag = document.createElement('script')

    tag.src = 'https://www.youtube.com/iframe_api'
    const firstScriptTag = document.getElementsByTagName('script')[0]
    firstScriptTag.parentNode.insertBefore(tag, firstScriptTag)

    window.onYouTubeIframeAPIReady = this.initPlayer.bind(this)
  }

  initPlayer() {
    this.player = new YT.Player('player', {
      height: '390',
      width: '640',
      videoId: 'AqjIWmiAidw'
    })
  }

  playVideo() {
    this.setState(prevState => ({
      hidden: false
    }))
    this.player.playVideo()
  }

  render() {
    return (
      <StyledDiv onClick={this.playVideo} className={ this.state.hidden ? '' : 'playing' } {...this.props}>
        <StyledImg
          src={thumbnail}
          alt="Aragon"
        />
        <div className={ this.state.hidden ? 'hidden' : '' }>
          <div id="player"></div>
        </div>
        <div className="icon"></div>
      </StyledDiv> 
    )
  }
}

export default Video
